import { createSlice } from '@reduxjs/toolkit';


const jobSlice  = createSlice({
  name: 'jobs',
  initialState: {
    results: [],
  },
  reducers: {
    setJobs: (state, action) => {
      state.results = action.payload;
    }
  }
});


export default jobSlice.reducer;
export const { setJobs } = jobSlice.actions;
