import { configureStore } from '@reduxjs/toolkit';
import favouriteSlice from '../slices/favouriteSlice';
import jobSlice from '../slices/jobSlice';

const bigReducer = {
  favourite: favouriteSlice,
  job: jobSlice,
};

const store = configureStore({
  reducer: bigReducer,
});

export default store;
